command.script:
write-host "Verify Database Model"

$db_model = Invoke-Sqlcmd -ServerInstance $env:COMPUTERNAME -Query "SELECT recovery_model_desc FROM sys.databases WHERE name = 'model'"

if ($db_model[0] -eq "SIMPLE")
{   
    Write-Host "Expected Database model for test environment:" $db_model[0]
    exit 0
}
else
{
    Write-Host "Unexpected Database model for test environment:" $db_model[0]
    exit 1
}
