command.script:
write-host "Checking SQL Authentication mode"

$sqlAuth = Invoke-Sqlcmd -ServerInstance $env:COMPUTERNAME -Query "SELECT SERVERPROPERTY('IsIntegratedSecurityOnly');"

if ($sqlAuth[0] -eq "0")
{   
    Write-Host "SQL Authentication is set to 'Windows and SQL Server Authentication mode' ."
    exit 0
}
else
{
    Write-Host "Unexpected SQL Authentication mode. Start Changing to 'Windows and SQL Server Authentication mode' and restart MSSQLSERVER services"
    Set-ItemProperty -Path "HKLM:\SOFTWARE\Microsoft\Microsoft SQL Server\MSSQL12.MSSQLSERVER\MSSQLServer" -Name "LoginMode" -Value "2"
    Restart-Service -Force MSSQLSERVER
    exit 0
}
