command.script:
write-host "Create Temp Database @option.CLIENT_CODE@_DEV1_MAIN"

Invoke-Sqlcmd -ServerInstance $env:COMPUTERNAME -Query "IF NOT EXISTS (SELECT name FROM sys.databases WHERE name = N' @option.CLIENT_CODE@_DEV1_MAIN') CREATE DATABASE [@option.CLIENT_CODE@_DEV1_MAIN]"

$checkMDF = test-path "D:\MSSQL\Data\@option.CLIENT_CODE@_DEV1_MAIN.mdf"
$checkLDF = test-path "D:\MSSQL\Log\@option.CLIENT_CODE@_DEV1_MAIN_log.ldf"

if ($checkMDF -eq $true -and $checkLDF -eq $true)
{   
    Write-Host "Both @option.CLIENT_CODE@_DEV1_MAIN.mdf and @option.CLIENT_CODE@_DEV1_MAIN.ldf are existed."
    Write-Host "Deleting Database @option.CLIENT_CODE@_DEV1_MAIN."
    Invoke-Sqlcmd -ServerInstance $env:COMPUTERNAME -Query "IF EXISTS (SELECT name FROM master.dbo.sysdatabases WHERE name = N'@option.CLIENT_CODE@_DEV1_MAIN' ) BEGIN DROP DATABASE [@option.CLIENT_CODE@_DEV1_MAIN] END ELSE PRINT N'@option.CLIENT_CODE@_DEV1_MAIN is not exist'"
    exit 0
}
else
{
    Write-Host "@option.CLIENT_CODE@_DEV1_MAIN.mdf or @option.CLIENT_CODE@_DEV1_MAIN.ldf has not been created ! "
    exit 1
}
