command.script:
write-host "Checking SQL Version"

$sqlVersion = Invoke-Sqlcmd -ServerInstance $env:COMPUTERNAME -Query "SELECT SERVERPROPERTY('productversion');"

if ($sqlVersion[0] -eq "12.0.5546.0")
{   
    Write-Host "Expected SQL version. Current Version is 12.0.5546.0. "
    exit 0
}
else
{
    Write-Host "Unexpected SQL version. Current Version is" $sqlVersion[0] 
    exit 1
}
