command.script:
Invoke-Sqlcmd -ServerInstance $env:COMPUTERNAME -Query "Declare @loginName VARCHAR(50) Declare @SqlStatement NVARCHAR(100) Set @loginName = '@option.CLIENT_CODE@'; Set @SqlStatement = N'use [MASTER] create login [' + @loginName + '] From Windows with DEFAULT_DATABASE=[MASTER]';
If not Exists (select name from sys.server_principals where name = @loginName) Begin print N'The login ' + @loginName + N' does not exist. Creating login now...' EXEC sp_executesql @SqlStatement End
If Exists (select name from sys.server_principals where name = @loginName) print N'The login ' + @loginName + N' has been created.' Else RAISERROR('Failed to create login', 16, 1) WITH NOWAIT " -ErrorAction Stop -Verbose
