command.script:
$AllMTDirs = (Get-ChildItem "D:\CharlesRiver" | ?{ $_.PSIsContainer } | ?{$_.Name -like '*serverapps*'})

foreach($dir in $AllMTDirs)
{ 
    Write-Host "D:\CharlesRiver\$dir\lib\hostingdomains.jar"
    if (Test-Path "D:\CharlesRiver\$dir\lib\hostingdomains.jar")
    {   
        Write-Host "Files were found."
    }
        else
    {
        Write-Host "Files were not found. The files have now been copied over"
        Copy-Item \\820963-USFS1\US1_Software\Utilities\hostingdomains.jar D:\CharlesRiver\$dir\lib
    }
}
