command.script:
if($env:JAVA_OPTS -eq "-Djsse.enableCBCProtection=false" -and $env:PERL_INLINE_JAVA_EXTRA_JAVA_ARGS -eq "-Djsse.enableCBCProtection=false")
{
    Write-Host "JAVA_OPTS and PERL_INLINE_JAVA_EXTRA_JAVA_ARGS have been setup correctly"
    exit 0
}else
{
    Write-Host "JAVA_OPTS and PERL_INLINE_JAVA_EXTRA_JAVA_ARGS are not correct"
    Write-Host "Reseting JAVA_OPTS to -Djsse.enableCBCProtection=false and PERL_INLINE_JAVA_EXTRA_JAVA_ARGS to -Djsse.enableCBCProtection=false"
    [Environment]::SetEnvironmentVariable("JAVA_OPTS", "-Djsse.enableCBCProtection=false", "Machine")
    [Environment]::SetEnvironmentVariable("PERL_INLINE_JAVA_EXTRA_JAVA_ARGS", "-Djsse.enableCBCProtection=false", "Machine")
    exit 0
}
